#coding:utf-8

"""
ID:          issue-7664
ISSUE:       https://github.com/FirebirdSQL/firebird/issues/7664
TITLE:       DROP TABLE executed for a table with big records may lead to "wrong page type" or "end of file" error
DESCRIPTION:
NOTES:
    Confirmed bug on 3.0.11.33691, 4.0.3.2957, 5.0.0.1100.
    Checked on 3.0.11.33695, 4.0.3.2966, 5.0.0.1121: all OK.
    NB: we have to specify "io_enc = locale.getpreferredencoding()" in the act.execute() in order localized message
    about EOF reaching be properly displayed (in case of regression or test builds where this bugf was not fixed).
"""
import locale
import pytest
from firebird.qa import *

db = db_factory()

test_script = f"""
    set list on;
    create table t1_big (id integer, str1 varchar(20000));
    commit;
    insert into t1_big values (0, '');
    commit;
    update t1_big set str1 = 'abc';
    commit;
    drop table t1_big;
    commit;
    select count(*) as chk_cnt from rdb$relations where rdb$relation_name = upper('t1_big');
"""

act = isql_act('db', test_script)

expected_stdout = """
    CHK_CNT                         0
"""

@pytest.mark.version('>=3.0.11')
def test_1(act: Action):
    act.expected_stdout = expected_stdout
    act.execute(combine_output = True, io_enc = locale.getpreferredencoding())
    assert act.clean_stdout == act.clean_expected_stdout
